import React, { Component } from "react";
import "./Style.css";
import VoteAmount from "./VoteAmount";

export default class CardComponent extends Component {
  state = {
    amount: 0,
  };
  handleCallback = (check_vote) => {
    const { amount: count } = this.state;
    let counter_new;
    if (check_vote === "Vote" && count < 10) {
      counter_new = count + 1;
    } else if (check_vote === "Unvote" && count > 0) {
      counter_new = count - 1;
    } else {
      return;
    }

    this.setState({ amount: counter_new });
  };
  render() {
    const { amount } = this.state;
    return (
      <div className="container">
        <div className="col">
          <div className="foodInfo">
            <h2>{this.props.fType}</h2>
            <h3>{this.props.fname}</h3>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc
              pellentesque at mi quis placerat. Curabitur porta, ante nec
              sollicitudin porta, lectus lorem malesuada nisi, aliquet varius
              urna ipsum et lectus. Quisque tempor odio tortor, ut egestas nisl
              dapibus ut.
            </p>
          </div>
          <div>
            <img
              className="Img"
              src={this.props.fImg}
            />
          </div>
        </div>
        <VoteAmount amount={amount} parentCallbcak={this.handleCallback} />
      </div>
    );
  }
}
