import React, { Component } from "react";
import "./Style.css";

export default class VoteAmount extends Component {
    constructor(Vote) {
      super(Vote);
    }
    handleClick = (vbtn) => {
      this.props.parentCallbcak(vbtn);
    };
    render() {
      function Check_Max (c){
        if (c>=10){
          alert("You can't vote anymore");
        }
      }
      function checkStatusRE (c){
        if (c<=0){
          alert("You can't unvote anymore");
        }
      }
      return (
        <div className="vote">
          <div className="row">
            <button className="btn" onClick={()=>{this.handleClick("Vote");Check_Max(this.props.amount) }}>Click to Vote</button>
            <div className="showbtn">{this.props.amount==0?"MIN":this.props.amount==10?"MAX":this.props.amount}</div>
            <button className="btn" onClick={()=>{this.handleClick("Unvote");checkStatusRE(this.props.amount)}}>Click to Unvote</button>
          </div>
        </div>
      );
    }
  }
